mapper.select:
SELECT #{keywordsParam} as keywordsParam, #{disabledParam} as disabledParam, p.* FROM TstProject p WHERE p.orgId=#{orgId} AND p.type='group'
AND p.name LIKE CONCAT('%','${keywordsParam}','%' )
AND p.disabledParam = #{disabledParam}

SELECT c.* FROM TstProject c WHERE c.parentId = #{id} AND c.type='project'

      AND c.name LIKE CONCAT('%','${keywordsParam}','%' )


      AND c.disabled = #{disabledParam}

mapper.update:
UPDATE TstProject
SET name=#{name}, descr=#{descr}, orgId=#{orgId}, parentId=#{parentId}, disabled=#{disabled}
WHERE id=#{id}

UPDATE TstProject
SET disabled=FALSE
WHERE id=#{id}

UPDATE TstProject
SET disabled=FALSE
WHERE parentId=#{id}

UPDATE TstProject
SET disabled=TRUE
WHERE parentId=#{id}
